package Loops;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*do{
		 * code to execute
		 * }
		 * while(condition)
		 * 
		 * 
		 * 
		 * 
		 * ForLoop: When iterations are fixed
		 * While: When iterations are not fixed
		 * do while: When we have to execute code at least once
		 */

		int a=5;
		do {
			
			System.out.println(a);
		}
		while(a<4);
		
		
	}

}


